import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/Navbar.css';
import ThemeToggle from './ThemeToggle';

function Navbar() {
  const [scrolled, setScrolled] = useState(false);
  const [expanded, setExpanded] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Fermer le menu mobile lors du changement de page
  useEffect(() => {
    setExpanded(false);
  }, [location]);

  return (
    <nav className={`navbar navbar-expand-lg ${scrolled ? 'navbar-scrolled' : ''}`}>
      <div className="container">
        <Link className="navbar-brand" to="/">
          <div className="logo-container">
            <img src="/logo-adiram.svg" alt="ADIRAM Logo" height="60" className="logo-image" />
            <span className="ms-2 logo-text">ADIRAM</span>
          </div>
        </Link>
        <button 
          className={`navbar-toggler ${expanded ? 'expanded' : ''}`} 
          type="button" 
          onClick={() => setExpanded(!expanded)}
          aria-expanded={expanded}
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className={`collapse navbar-collapse ${expanded ? 'show' : ''}`} id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <ThemeToggle />
            <li className="nav-item">
              <Link className={`nav-link ${location.pathname === '/' ? 'active' : ''}`} to="/">
                Accueil
                <span className="nav-indicator"></span>
              </Link>
            </li>
            <li className="nav-item">
              <Link className={`nav-link ${location.pathname === '/realisations' ? 'active' : ''}`} to="/realisations">
                RÃ©alisations
                <span className="nav-indicator"></span>
              </Link>
            </li>
            <li className="nav-item">
              <Link className={`nav-link ${location.pathname === '/login' ? 'active' : ''}`} to="/login">
                Connexion
                <span className="nav-indicator"></span>
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link btn-signup" to="/signup">Inscription</Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
