const { sendMail } = require("../../util/mailer");

class EmailService {
    constructor() {
    }

    /**
     * Envoie une notification de test
     */
    async sendtest(Data) {

      try {
        // Notification à l'utilisateur
        await sendMail({
          to: Data.email,
          subject: Data.sujet,
          html: "Bonjour, votre test a été traité avec succès.",
        });
      } catch (error) {
        console.error("Erreur envoi notifications test", {
          error: error.message,
        });
      }
    }
}

module.exports = new EmailService();
